  Tone.Transport.bpm.value = 140;
  Tone.Transport.start();
  loopBeat.start(0);

function song(time){


  if (counter%3 === 0){
  bassSynth.triggerAttackRelease('F#1', '8n', time, 1.2);
  }

  if (counter%2 === 0){
    bassSynthSlow.triggerAttackRelease('C#1', '8n', time, 1.2);
    }



  if (counter%4 !== 1){

    if (counter === 3 || counter === 12){
      cymbalSynth.envelope.decay = 0.5;
      plucky.triggerAttack('C1', time, 2);
      fmSynth.triggerAttackRelease('D5', '64n', time);

    } else {
      cymbalSynth.envelope.decay = 0.01;
      fmSynth.triggerAttackRelease('D5', '128n', time);
    }

    cymbalSynth.triggerAttackRelease('32n', time, 0.3);

  }

  counter = (counter + 1)%16;

}


