function drawFields(){

	l = 0.01;
	adj = 20;

	let c1 = color(noise(50 * .1) * 73, 111, 6); // #66990b
	let c2 = color("#d8c978");

	noLoop();

	for (let x = 0; x < width; x++) {

		for (let y = 0; y < height; y++) {

			// lerp code adjusted from https://github.com/hugomagallanes

			noiseDetail(10, 0.5);

			let n = noise(x * l, y * l);
			let rn = n * adj - floor(n * adj);

			let a = map(rn, 0, 0.5, 0, 1)

			if (rn > 0.5){
				a = map(rn, 0.5, 1, 1, 0);
			}

			let c = lerpColor(c2, c1, a);

			stroke(c);

			beginShape();

			curveVertex(x + n, y + n);
			curveVertex(x + n, y + n);
			curveVertex(x + n, y + n);
			curveVertex(x + n, y + n);

			endShape();

		}

	}

}

