let n;
let g;
let x1;
let y1;
function sun() {
  const iters = 15;
  const cX = 100;
  const cY = 85;
  const r = n * 50;
  for (let iter = 0; iter <= iters; iter++) {
    n = noise(iter + millis() * 0.001);

    const a1 = map(iter - 1, 0, iters, 0, 2 * PI);
    let a2 = map(iter, 0, iters, 0, 2 * PI);
    if (a2 >= 2 * PI) a2 -= 2 * PI;
    x1 = cX + sin(a1) * r;
    let x2 = cX + sin(a2) * r;
    y1 = cY + cos(a1) * r;
    let y2 = cY + cos(a2) * r;
    translate(-5, 0);

    for (let i = 0; i < 5; i++) {
      line(x1 * i, y1 * i, x2 * i, y2 * i);
      stroke(220, 220, 200, 20);
      strokeWeight(1);
    }
  }
}

