const storyGrammar = {
    'top': ['#div#'],
    'div': ['<div style="border: #borderSize# #borderType# #borderColor#;  color: #borderColor#; text-align: center; font-family: serif; margin: 10px;">#content#</div>'],
    'content': ['#div#', '#div# #div#', '#word#', '#word#'],
    'borderSize': ['1px', '2px', '3px', '4px', '5px', '6px', '7px', '8px', '9px', '10px'],
    'borderType': ['dotted', 'dashed', 'outset', 'inset', 'ridge', 'groove', 'double', 'solid'],
    'borderColor': ['black'],
    'word': ['<span style="font-size: 20px">a #adj# #things# #adv# #verb# to the #places# </span>'],
    'adj': ['brave', 'calm', 'colorful', 'beautiful', 'greedy', 'helpful', "cute", "kind", "smart"],
    'things': ['TV', 'phone', "notepad", "car", "pen", "cup", "coat", "finger", "necklace", "shoe"],
    'adv': ['fondly', 'foolishly', "warmly", "sweetly", "sweetly", "furiously", "madly", "wisely"],
    'verb': ['goes', 'hopes', "runs", "eats", "sweats", "slaps", "runs", "arrives", "avoids", "becomes", "stays"],
    'places': ['store', 'hospital', "hills", "beach", "restaurant", "library", "class", "river", "highway", "country side"],
}


function main() {
    let grammar = tracery.createGrammar(storyGrammar);
    let story = grammar.flatten('#top#');

    const storyDiv = document.createElement('div');
    storyDiv.style = "position: relative; top: -650px;"
    storyDiv.innerHTML = story;

    document.body.insertAdjacentElement('beforeend', storyDiv);
}


setTimeout(main, 10);

