/**Animated circles class **/
class Circle {

  constructor(circleColor, animationStartDelay) {
    //properties: circle-related variables
    this.x = width / 2;
    this.y = height / 2;
    this.radius = 50;
    this.color = circleColor;
    this.animationStartDelay = animationStartDelay;
  }

  render() {
    if (theta > this.animationStartDelay) {
      fill(this.color)
      noStroke()
      ellipse(this.x, this.y, this.radius)
    }
  }

  animate() {
    if (theta > this.animationStartDelay) {
      this.radius += 10;
    }
  }

}

