function setup() 
{
	createCanvas(400, 400);

    img.loadPixels();
}

function draw()
{
    background(94, 87, 81);

    for (x = 0; x < img.width; x++) {
        for (y = 0; y < img.height; y++) {

            let pixelRed = getQuick(img, x, y)[0];
            let pixelGreen = getQuick(img, x, y)[1];

            if (random(255) < pixelRed) {
                drawGrassBlade(x, y);
            }

            if (random(255) < pixelGreen){

                drawFlowers();

            }
        }
    }

    noLoop();

}

// drawGrassBlade() adapted from https://compform.net/pixels/

function drawGrassBlade(x, y) {

    stroke(255, 0, 0);
    fill(255, 255, 255);
    stroke(5, random(150), random(40), random(80));

    bladeHeight = min(
        random(1, 60), random(1, 60), random(1, 60),
        random(1, 60), random(1, 60), random(1, 60)
    );

    bladeLean = random(-.3, .3);
    bladeLean *= bladeHeight;

    line(x, y, x + bladeLean, y - bladeHeight);

}

function drawFlowers(){

    fill(255, 255, 255, random(40, 100))
    noStroke();
    let r = random(2, 4);
    ellipse(x + bladeLean, y - bladeHeight, r, r);

}

function getQuick(img, x, y) {

    var i = (y * img.width + x) * 4;
    return [
        img.pixels[i],
        img.pixels[i + 1],
        img.pixels[i + 2],
        img.pixels[i + 3],
    ];
}

