function mouseReleased() {
  dots.push(new Dot(mouseX, mouseY));
}

function spawnNear(x, y, r) {
  let p = randomPointOnCircle();
  dots.push(new Dot(x + p.x * r, y + p.y * r));
}

function randomPointInCircle() {
  let r = sqrt(random());
  let a = random() * 2 * PI;
  return { x: cos(a) * r, y: sin(a) * r };
}

function randomPointOnCircle() {
  let r = 1;
  let a = random() * 2 * PI;
  return { x: cos(a) * r, y: sin(a) * r };
}

class Dot {
  constructor(x = 0, y = 0) {
    this.x = constrain(x, 0, width);
    this.y = constrain(y, 0, height);
    this.dY = 0;
    this.age = 0;
    this.size = 0.2;
  }

  step(dTime) {
    this.age += dTime;

    // spread
    dots.forEach((other) => {
      if (other === this) return;
      let d = dist(this.x, this.y, other.x, other.y);
      if (d > 100) return;

      let a = atan2(this.y - other.y, this.x - other.x);

      //   let force = map(d, 0, 100, 1, 0, true);
      let force = 1 * this.size;
      this.x += cos(a) * force;
      this.y += sin(a) * force;
    });

    // fall
    // this.dY += 0.1;
    // this.y += this.dY;
    // if (this.y > height) this.dY = -abs(this.dY);

    // spawn new dots
    if (this.age - dTime < 0.05 && this.age >= 0.05) {
      spawnNear(this.x, this.y, 30 * this.size);
    }

    // remove old dots
    if (this.age > 2) {
      dots.splice(dots.indexOf(this), 1);
    }
  }

  draw() {
    push();

    // draw center dot
    // let radius = map(this.age, 1.8, 2, 15, 0, true);
    // radius = max(radius, 0);
    let radius = 15;
    let alpha = map(this.age, 1, 2, 255, 0);
    stroke(255, alpha);
    noFill();
    jitter_ellipse(this.x, this.y, radius * this.size, radius * this.size);

    // draw ripples
    for (let n = 0; n < 1; n += 0.2) {
      let radius = map(this.age - n, 0, 1, 10, 100);
      radius = max(radius, 0);
      let alpha = map(this.age - n, 0, 1, 255, 0);
      noFill();
      stroke(255, alpha);
      jitter_ellipse(this.x, this.y, radius * this.size, radius * this.size);
    }

    pop();
  }
}

function jitter_ellipse(x, y, w, h, j = 0) {
  x += random(-j, j);
  y += random(-j, j);
  w += random(-j, j);
  h += random(-j, j);
  ellipse(x, y, w, h);
}

