class InnerCircle {
  constructor(cx, cy, r, rotate, colorr, interval) {
    this.cx = cx;
    this.cy = cy;
    this.r = r;
    this.rotate = rotate;
    this.colorr = colorr;
    this.interval = interval;
    this.colorrSelection = colorrSelection;
    setInterval(() => this.changeRotate(), interval);
    setInterval(() => this.changeColor(), interval*4);

    // setInterval(() => this.changeStatus(), 1000);
  }

  changeRotate() {
    g += .1;
    //this.colorr = random(255)
  }

  changeColor() {
    this.colorr = random(255);
    this.colorrSelection = random(["rr", "gg", "bb"]);

  }

  draw() {
    rotate(this.rotate + g);
    translate(this.cx, this.cy);
      this.colorrSelection === "rr" ? stroke(this.colorr, 0, 0) : stroke(100, 0, 0);
      ellipse(this.cx, this.cy, this.r);
    this.colorrSelection === "gg" ? stroke(0, this.colorr, 0) : stroke(0, 100, 0);
    line(0, this.r, 0, 0);
    this.colorrSelection === "bb" ? stroke(0, 0, this.colorr) : stroke(0, 0, 100);
    strokeWeight(3);
    //line(0, this.r, 0, 0);
    line(0, -this.r, 0, 0);
    //fill(10);

  }
}

