var myTurtle;

function setup() {
    createCanvas(windowWidth, windowHeight);
    noFill();
    stroke("#fbff12");
    background("#1f2041");
    noLoop();

    colorMode(HSB);

    myTurtle = new Turtle();
}

function drawLeaf(newColor) {
	myTurtle.pushState();

    stroke(newColor);
    strokeWeight(2);
	for (i = 0; i < 10; i++) {
		myTurtle.moveForward(2);
		myTurtle.turnLeft(20);
	}
	strokeWeight(1);
    stroke("#fbff12");
	myTurtle.popState();
}

function drawBranch(length, reflect, newColor) {

    if (length < 4) {
        drawLeaf(newColor);
        return;
    }

    // draw this branch
    strokeWeight(length / 10);
    myTurtle.moveForward(length * .5);
    reflect ? myTurtle.turnRight(10) : myTurtle.turnLeft(10);
    myTurtle.moveForward(length * .5);

    // left child
    myTurtle.pushState();
    myTurtle.turnLeft(random(20, 40));
    drawBranch(length * 0.75, reflect, newColor);
    myTurtle.popState();

    // right child
    myTurtle.pushState();
    myTurtle.turnRight(random(10, 30))
    drawBranch(length * 0.75, reflect, newColor);
    myTurtle.popState();

}

function drawTree(x, y, theta, h = 100, reflect = false, newColor = "#0080002E"){

    myTurtle.penUp();
    myTurtle.moveTo(x, y);
    myTurtle.turnTo(theta);
    myTurtle.penDown();
    drawBranch(h, reflect, newColor);
}

function drawForest(){

    baseline = height-50;
    drawTree(70, baseline, -70, 80, false, "#41ead42E");
    drawTree(width-130, baseline, -90, 50, true, "#bcf4de2e");
    drawTree(width-530, baseline, -90, 70, true, "#ff206e2E");
    drawTree(width-35, baseline,-100, 100, true, "#41ead42E");
    drawTree(width/2+90, baseline,-100, 40, true, "#ffffff2E");
    drawTree(width/2, baseline, -90, 110, true, "#b88ae62E");
}

function drawBar(){
    myTurtle.turnTo(-90);
    myTurtle.moveForward(80);
    myTurtle.turnTo(-45);
    myTurtle.moveForward(20);
    myTurtle.turnTo(45);
    myTurtle.moveForward(20);
    myTurtle.turnTo(90);
    myTurtle.moveForward(80);

}

function drawFence(){
    myTurtle.penUp();
    myTurtle.moveTo(0, height);
    myTurtle.penDown();
    stroke("#c97c5d")
    strokeWeight(30);
    for(let i = 0; i<50; i++){
        drawBar();
        myTurtle.penUp();
        myTurtle.turnTo(0);
        myTurtle.moveForward(40);
        myTurtle.penDown();
    }
    strokeWeight(1);
}

function drawCircle(){
    for(let i = 0; i<36; i++){
        myTurtle.turnRight(10);
        myTurtle.moveForward(8);
    }
}

function drawMoon(){
    myTurtle.penUp();
    myTurtle.moveTo(160, 100);
    myTurtle.penDown();
    stroke("#cccccc")
    strokeWeight(100);

    for(let i = 0; i<1; i++){
        drawCircle();
        myTurtle.penUp();
        myTurtle.turnTo(0);
        myTurtle.moveForward(50);
        myTurtle.penDown();
    }
    strokeWeight(1);
}

function drawStars(){
    for(let k=0; k<150; k++){
        myTurtle.penUp();
        myTurtle.moveTo(random(10, width-10), random(10, height/3*2));
        myTurtle.penDown();
        stroke("#ffffff")
        strokeWeight(3);

        for(let i = 0; i<1; i++){
            myTurtle.moveForward(1);
            myTurtle.penUp();
            myTurtle.turnTo(0);
            myTurtle.moveForward(50);
            myTurtle.penDown();
        }
        strokeWeight(1);
    }
}

function draw() {
    drawStars();
    drawForest();
    drawFence();
    drawMoon();
}

