function stepBall(b) {
	b.x = b.x + b.dX;
	b.y = b.y + b.dY;

	if (b.x > width - 50) {
        b.dX = -abs(b.dX);
        piano_1.play(0,random(0.5, 2),1,0);
    }

	if (b.y > height - 50) {
        b.dY = -abs(b.dY);
        piano_1.play(0,random(0.5, 2),1,0);
    }

	if (b.x < 0) {
        b.dX = abs(b.dX);
        piano_2.play(0,random(0.5, 2),1,0);
    }

	if (b.y < 0) {
        b.dY = abs(b.dY);
        piano_2.play(0,random(0.5, 2),1,0);
    }
}

