    const sumMinusMean = Ratings.reduce(
      (a, b) => a + (b - mean) * (b - mean),
      0
    );
    //console.log(sum);
    return {
      mean: mean,
      sd: Math.sqrt(sumMinusMean / (Ratings.length - 1)),
      range: [Math.min(...Ratings), Math.max(...Ratings)],
    };
  }
