var values = ['#55FA38', '#E68E81', 'blue', '#E002B3', '#E01700'];

// create a variable to hold the current position in the deck
var position = 0;
var x = 0;
var ellipseHeight = 1;
var ellipseWidth = 20;
var drawLimit = 500;
var bg = ['#FAF5CD', '#CDF2FA', '#CDFADA', '#D1D1FF', '#FFD5D1'];
var bgpos = 0;


function setup() {

  createCanvas(500, 500);
  background(0);
  // shuffle the deck(values) first
  bg = shuffle(bg);
  values = shuffle(values);
 angleMode(DEGREES);

  frameRate(10);
}

function mousePressed() {
  background(getColor());
  // background(shuffleBackground());
}

function draw() {

noStroke();



  let ellipseHeight = floor(ShuffledHeight());

  //draw limit = ellipses appears in a loop when it crosses the canvas
  drawLimit -= 1;
  if (drawLimit >= 0) {
    x += 20;
    ellipseHeight = floor(15 * random(10));
    if (x > width) {
      x = 0;
    }
    //random fill colours
    fill(random(200,255),random(100,240),120);
    ellipse(x,  height * 0.4 - ellipseHeight-20, 3, ellipseHeight); //layer 1 overlap
    ellipse(x, 200, 9, ellipseHeight-random(50,100)); //layer 2
    ellipse(x, 450, 9, ellipseHeight-random(50,100)); //layer 4

    //shuffled colors
    fill(shuffleBackground()); 
    ellipse(x, height * 0.9 - ellipseHeight-20, 5, ellipseHeight+random(10,0));//layer 3
    ellipse(x,  height * 0.4 - ellipseHeight-50, 3, ellipseHeight); //layer 3 overlap
     ellipse(x,  height * 0.2 - ellipseHeight-20, 7, ellipseHeight+random(50,80)); //layer 1

    /////rotate
    push();
    rotate(90);
    translate(10,-490);
    fill(random(230, 255), random(110, 255), random(180, 255));
    ellipse(x,  height * 0.4 - ellipseHeight-20, 3, ellipseHeight); //layer 1 overlap
    ellipse(x, 200, 9, ellipseHeight-random(50,100)); //layer 2
    ellipse(x, 450, 9, ellipseHeight-random(50,100)); //layer 4

    //shuffled colors
    // fill(shuffleBackground()); 
    fill(getColor()); 
    ellipse(x, height * 0.9 - ellipseHeight-20, 5, ellipseHeight+random(10,0));//layer 3
    ellipse(x,  height * 0.4 - ellipseHeight-50, 3, ellipseHeight); //layer 3 overlap
     ellipse(x,  height * 0.2 - ellipseHeight-20, 7, ellipseHeight+random(50,80)); //layer 1
    pop();
  }
}

function ShuffledHeight() {

  //even distribution - floor the height of ellipses randomly
  return random(0, 10);
}

function getColor() {
  // find the value at the current position in the deck
  let v = values[position];

  // change the position for next time
  position++;

  // if we run out of "cards", shuffle and start over from the top
  if (position == values.length) {
    values = shuffle(values);
    position = 0;
  } 

  return v;
}

