// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.2.0/p5.js

var gridSize = 5;
var data = [];

const dim = 600;


function setup() {
  createCanvas(dim, dim);
  init();
}

function init() {

  // initialize 2D array
  data = [];
  for (let i = 0; i < gridSize; i++) {
    data[i] = [];
    for (let j = 0; j < gridSize; j++) {
      data[i][j] = false;
    }
  }

  // Assign random blocks
  let lights = int(random(1, 5));
  while (lights) {
    let x = int(random(gridSize));
    let y = int(random(gridSize));

    if (!data[x][y]) {
      data[x][y] = true;
      lights--;
    }
  }

  // Draw Blocks
  drawLayout();

}

function drawLayout() {
  strokeWeight(4);
  stroke('yellow');

  for (let i = 0; i < gridSize; i++) {
    for (let j = 0; j < gridSize; j++) {

      if (data[i][j])
        fill(200,0,255);//pink

      else
        fill(0,200,255);//blue

      let blockSize = dim / gridSize-10;
     ellipse(60+blockSize * i,65+ blockSize * j, blockSize, blockSize);
    }
  }
}

function mousePressed() {
  let i = int(mouseX / width * gridSize);
  let j = int(mouseY / height * gridSize);

  toggle(i, j);
  toggle(i-1, j);
  toggle(i+1, j);
  toggle(i, j-1);
  toggle(i, j+1);

}

function toggle(i, j) {
  if ((i >= 0 && i < gridSize) && (j >= 0 && j < gridSize))
    data[i][j] = !data[i][j];
}

function draw() {
  drawLayout();
}

