	img.textFont(myFont);
	img.textSize(400);
	img.translate(width / 2, height / 2);
	img.textAlign(CENTER, CENTER);
}

function draw() {
	background(0);

	imgWindow.width = windowWidth/slider.value();
	imgWindow.height = windowHeight/slider.value();

	let delta = radians(theta);
  let translateX = scalar * cos(delta);

	img.background(255);
	push();
	img.translate(translateX, 0);
	pop();
	img.text("COMP\nFORM", 0, 0);

	for(let i = slider.value(); i > 1; i--){
		imageMode(CENTER);
    push();
    translate(width / 2, height / 2);
		image(img, 0, 0, imgWindow.width*i, imgWindow.height*i);
		pop();
	}

	theta += 2;
}

