//Collision detection - Bouncing behavior

var circles;
var boxes;
var sasa;
let sasa2;
let sasaa;

function preload() {
  f = loadFont('DIN-BlackItalic.otf');
}

function setup() {
  createCanvas(800, 400);
  sasaa = new Group();
  let sasa = createSprite(random(0, 90), random(0, 90), 5, 5);
  sasa.setCollider('circle', -2, 2, 20);
  sasa.addAnimation('normal', 'Asset 2.png', 'Asset 2-1.png');
  sasaa.add(sasa);

  let sasa2 = createSprite(random(750, width), random(350, height), 5, 5);
  sasa2.setCollider('circle', -2, 2, 20);
  sasa2.addAnimation('normal', 'Asset 3.png', 'Asset 3-1.png');
  sasaa.add(sasa2);

  circles = new Group();

  for (var i = 0; i < 60; i++) {
    var circle = createSprite(random(0, width), random(0, height), 50, 50);
    circle.addAnimation('normal', 'Untitled-2-01.png', 'Untitled-2-15.png');
    circle.setCollider('circle', -2, 2, 25);
    circle.setSpeed(random(2, 3), random(0, 360));

    circle.scale = random(0.5, 1);

    circle.mass = circle.scale;
    circles.add(circle);
  }

  boxes = new Group();

  for (var j = 0; j < 5; j++) {
    var box = createSprite(random(0, width), random(0, height));
    box.addAnimation('normal', 'obstacle.png', 'obstacle-1.png');
    box.immovable = true;
    if (j % 2 == 0)
      box.rotation = 90;
    boxes.add(box);
  }
}

function draw() {
  background(255, 255, 255);

  //circles bounce against each others and against boxes
  circles.bounce(circles);
  //boxes are "immovable"
  circles.bounce(boxes);
  circles.displace(sasaa);
  boxes.displace(sasaa);

  //all sprites bounce at the screen edges
  for (var i = 0; i < allSprites.length; i++) {
    var s = allSprites[i];
    if (s.position.x < 0) {
      s.position.x = 1;
      s.velocity.x = abs(s.velocity.x);
    }

    if (s.position.x > width) {
      s.position.x = width - 1;
      s.velocity.x = -abs(s.velocity.x);
    }

    if (s.position.y < 0) {
      s.position.y = 1;
      s.velocity.y = abs(s.velocity.y);
    }

    if (s.position.y > height) {
      s.position.y = height - 1;
      s.velocity.y = -abs(s.velocity.y);
    }
  }

  var p1 = sasaa[0];
  if (keyDown(UP_ARROW)) {
    p1.position.y -= 10;
  }
  if (keyDown(DOWN_ARROW)) {
    p1.position.y += 10;
  }
  if (keyDown(LEFT_ARROW)) {
    p1.position.x -= 10;
  }
  if (keyDown(RIGHT_ARROW)) {
    p1.position.x += 10;
  }

  var p2 = sasaa[1];
  if (keyDown('w')) {
    p2.position.y -= 10;
  }
  if (keyDown('s')) {
    p2.position.y += 10;
  }
  if (keyDown('a')) {
    p2.position.x -= 10;
  }
  if (keyDown('d')) {
    p2.position.x += 10;
  }

  drawSprites();

  if (p2.overlap(p1)) {
    textSize(80);
    textAlign(CENTER);
    textFont(f);
    fill(252, 98, 68);
    text("NICE", width / 2, height / 2 - 20);
    fill(0, 169, 157);
    text("NICE", width / 2, height / 2 + 60);
  }
}


