const storyGrammar = {
    "story": ["It was a dark and stormy night when #phrase#. Then, #phrase#. Shortly thereafter, #phrase#, and #phrase#. Finally, #phrase#, and #phrase#. The End."],
    "phrase": ["#subject.a# #adverb# #verb# #object.a#"],
    "subject": ["#noun#", "#adjective# #adjective# #noun#"],
    "object": ["#noun#", "#adjective# #noun#", "#adjective# #noun# and a #adjective# #noun#"],
    "adverb": ["especially", "judgmentally", "powerfully", "quickly", "gratefully", "famously"],
    "adjective": ["angry", "happy", "magical", "cheerful", "disgusted", "doubtful", "foolish", "envious"],
    "noun": ["candle", "carpet", "mirror", "table", "bag", "sneaker", "television", "book"],
    "verb": ["laughs", "reads", "teaches", "lies to", "remembers", "sings", "yells at"],
}


function main() {
    let grammar = tracery.createGrammar(storyGrammar);
    let story = grammar.flatten("#story#");

    const storyDiv = document.createElement('div');
    storyDiv.style = "color: white; position: relative; top: -650px; font-size: 18px; margin: 22%; line-height: 1.2; font-family: sans-serif";
    storyDiv.textContent = story;

    document.body.insertAdjacentElement("beforeend", storyDiv);
}


setTimeout(main, 10);

