  var randomNumShapes = floor((random(8, 15) + random(8, 15))/2);
  for (let i = 0; i < randomNumShapes; i++){
    var randomSmallShapes = floor(max(random(5, 10), random(5, 10)))
    var randomHue = floor(random(256));

    var randomX = floor(random(100 ,501));
    var randomY = floor(random(0, 600));

    var randomW = floor(min(random(5, 150), random(5, 150)));
    var randomH = floor(min(random(5, 80), random(5, 80)));


    for (let k = 0; k < randomSmallShapes; k++){
      push()
      var posOrNeg = round(random()) * 2 - 1
      rotate((rotation + k * 5) * posOrNeg);
      translate(randomW / 2, randomH / 2)
      fill(randomHue+((k+1)*5), 100, 100);
      rect (randomX + (randomW * k), randomY + (randomH * k), randomW + ((k+5)*posOrNeg), randomH + ((k+5)*posOrNeg))
      pop()
    }

  }        
}

