    noStroke();
    noLoop();
    angleMode(DEGREES);
  }

  function draw() {
      background(255);
      push();
      translate (width/2, height/2);
      rotate(90);
      yellowDots()
      pop();
      push();
      translate (width/2, height/2);
      rotate(45);
      magentaDots();
      pop();
      push();
      translate (width/2, height/2);
      rotate(75);
      blueDots();
      pop();
      push();
      translate (width/2, height/2);
      rotate(105);
      blackDots();
      pop();
  }
  function yellowDots(){
    let n;
    for (let x = -400; x < 400; x += 10) {
        for (let y = -400; y < 400; y += 10) {
            n = noise(dist(300, 300, x, y) * .03);
            let coord = n / .1 + 2 ;
            ellipse(x,y,coord,coord)
            // rotate(90);
            blendMode(MULTIPLY);
            fill(255,255,0)
        }
    }
  }
  function magentaDots(){
