function generateCanvas() {
  clear();

  blockSize = dim / gridSlider.value();

  let tile = setTile(blockSize);

  let theta;

  for (let i = blockSize / 2; i < dim; i += blockSize) {
    for (let j = blockSize / 2; j < dim; j += blockSize) {
      theta = HALF_PI * int(random(0, 4)); // 0, PI/2, PI, 3/2 * PI
      push();
      translate(i, j);
      rotate(theta);
      image(tile, -blockSize / 2, -blockSize / 2);
      pop();
    }
  }
}

function setTile(size) {
  tile = createGraphics(size, size);
  tile.noStroke();
  tile.background('black');
  tile.fill('white');
  tile.beginShape();
  tile.vertex(0, 0);
  tile.vertex(0, size);
  tile.vertex(size, size);
  tile.endShape();

  return tile;
}

