function randomPointInCircle() {
  let r = sqrt(random());
  let a = random() * 2 * PI;
  return { x: cos(a) * r, y: sin(a) * r };
}

function randomPointOnCircle() {
  let r = 1;
  let a = random() * 2 * PI;
  return { x: cos(a) * r, y: sin(a) * r };
}

