  gridSlider.position(0, height);
  gridSlider.changed(generateCanvas);

  generateCanvas();
}

function draw() {

}


function generateCanvas() {
  clear();
  background(200,120,120)
  blockSize = dim / gridSlider.value();

  let tile = setTile(blockSize);

  let theta;

  for (let i = blockSize / 2; i < dim; i += blockSize) {
    for (let j = blockSize / 2; j < dim; j += blockSize) {
      // theta = HALF_PI * int(random(0, 4)); // 0, PI/2, PI, 3/2 * PI
      theta = HALF_PI * int(random(23,534)); // 0, PI/2, PI, 3/2 * PI
      push();
      translate(i, j);
      rotate(theta);

      image(tile, -blockSize / 2, -blockSize / 2);
      pop();
    }
  }
}

function setTile(size,n) {
  let x = 200;
  let y = 100;

  n = noise(x*120, y+390, millis() * 78.431);

  tile = createGraphics(size,n*size);
  tile.noStroke();
  tile.background('pink');
  tile.fill('green');
  tile.beginShape();
  tile.curveVertex(0, 0);
  tile.curveVertex(0, 0);
  tile.curveVertex(0, size);
  tile.curveVertex(230, size);
  tile.curveVertex(size, size);
  tile.curveVertex(size, size);
  tile.endShape();
  tile.fill('yellow');
  tile.ellipse(0,0,size,size-5); 
  tile.fill(10,120,230);
  tile.rect(400,200,5-size,10-size);
  tile.image(img2, 0,0,blockSize*n , blockSize*n);
  return tile;
}

