let layerofsnow = 600;
let maximum = 15;
let gravity = 0.95;
let layers = 5;

let speed = 10;
let change = 0.0003;

let snow = [];

function setup() {
  createCanvas(600, 600);
  noStroke();


  for (let l = 0; l < layers; l++) {
    snow.push([]);
    for (let i = 0; i < layerofsnow; i++) {
      snow[l].push({
        x: random(width),
        y: random(height),
        mass: random(0.75, 1.25),
        l: l + 1
      });
    }
  }
}

function draw() {
  background(0, 0, 102);

  // Iterate through each snowflake to draw and update them
  for (let l = 0; l < snow.length; l++) {
    const LAYER = snow[l];

    for (let i = 0; i < LAYER.length; i++) {
      const snow = LAYER[i];
      circle(snow.x, snow.y, (snow.l * maximum) / layers);
      updateSnow(snow);
    }
  }
}


function updateSnow(snow) {
  const diameter = (snow.l * maximum) / layers;
  if (snow.y > height + diameter) snow.y = -diameter;
  else snow.y += gravity * snow.l * snow.mass;

 let wind =
    noise(snow.l, snow.y * change, frameCount * change) -
    0.5;
  if (snow.x > width + diameter) snow.x = -diameter;
  else if (snow.x < -diameter) snow.x = width + diameter;
  else snow.x += wind * speed * snow.l;
}

