function draw() {

  let newPixels = [];


  img.loadPixels();


    for (let y = 0; y < height; y++) {
      for (let x = 0; x < width; x++) {


        const blurRadius = int(1);


        const index = (y * width + x) * 4;


        const leftIndex = index - 4 * blurRadius;
        const rightIndex = index + 4 * blurRadius;
        const upIndex = index - 4 * width * blurRadius;
        const downIndex = index + 4 * width * blurRadius;


        const middle = pixel(index);
        const left = pixel(leftIndex);
        const right = pixel(rightIndex);
        const up = pixel(upIndex);
        const down = pixel(downIndex);


        let r = min(min(min(min(middle.r, left.r), right.r), up.r), down.r);
        let g = min(min(min(min(middle.g, left.g), right.g), up.g), down.g);
        let b = min(min(min(min(middle.b, left.b), right.b), up.b), down.b);


        newPixels.push(r, g, b, 255);
      }
    }

  for(let i = 0; i<newPixels.length; i++){
   img.pixels[i] = newPixels[i]; 
  }





  img.updatePixels();


}


  index = wrapIndex(index);

  return {
    r: img.pixels[index + 0],
    g: img.pixels[index + 1],
    b: img.pixels[index + 2],
    a: img.pixels[index + 3]
  }
}

function wrapIndex(index) {

  let max = img.pixels.length;


  if (index > max) return index % max;


  if (index < 0) return (index % max) + max;


}

