// Rotating Triangles
// Inspired by Sem TÃ­tulo, Waldemar Cordeiro (1958)
// http://enciclopedia.itaucultural.org.br/obra4196/sem-titulo

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.1.9/p5.js

const dim = 800;
const smoothing = 0.1;

var gridSlider;
var grid = [];
var delta = [];
var blockSize = 0;
var tile;

var lastIndex = 0;

function setup() {
  let canvas = createCanvas(dim, dim);

  gridSlider = createSlider(5, 30, 15, 1);
  gridSlider.class('slider')
  gridSlider.position(0, height);
  gridSlider.changed(generateCanvas);

  generateCanvas();
  canvas.mouseMoved(getTile);
}

function draw() {
  clear();
  grid.forEach(function(position, index) {
    position.angle += delta[index] * smoothing;
    delta[index] -= delta[index] * smoothing;

    push();
    translate(position.x, position.y);
    rotate(position.angle);
    image(tile, -blockSize / 2, -blockSize / 2);
    pop();
  });
}


function generateCanvas() {
  clear();
  grid = [];
  delta = [];

  blockSize = dim / gridSlider.value();

  tile = setTile(blockSize);

  let theta;

  for (let i = blockSize / 2; i < dim; i += blockSize) {
    for (let j = blockSize / 2; j < dim; j += blockSize) {
      theta = HALF_PI * int(random(0, 4));
      grid.push({
        x: i,
        y: j,
        angle: theta
      });
      delta.push(0);
    }
  }
}

function setTile(size) {
  let tile = createGraphics(size, size);
  tile.noStroke();
  tile.background('black');
  tile.fill('white');
  tile.beginShape();
  tile.vertex(0, 0);
  tile.vertex(0, size);
  tile.vertex(size, size);
  tile.endShape();

  return tile;
}

function getTile() {
  let factor = blockSize / 2;
  let posX = mouseX;
  let posY = mouseY;

  grid.forEach(function(position, index) {
    if ((posX > position.x - factor && posX < position.x + factor) &&
      (posY > position.y - factor && posY < position.y + factor)) {
      if (lastIndex != index)
        delta[index] += HALF_PI;
      lastIndex = index;
    }
  });
}

function keyPressed() {
  if (keyCode == 32)
    saveCanvas('Rotating_Triangles_' + int(random(millis())), 'jpg');
}

