function valueFromDeck() {
  // find the value at the current position in the deck
  var v = values[position];
  colorMode(HSB)
  noStroke();
  fill(v * 11, 80, 80)

  osc.freq(v*100 + 300);

  // change the position for next time
  position++;

  // if we run out of "cards", shuffle and start over from the top
  if (position == values.length) {
    values = shuffle(values);
    position = 0;
  }

  // return the value
  return v;
}

function xCoordsFromDeck() {
  // find the value at the current position in the deck
  var x = xCoords[xPosition];

  // change the position for next time
  xPosition++;

  // if we run out of "cards", shuffle and start over from the top
  if (xPosition == xCoords.length) {
    x = shuffle(xCoords);
    xPosition = 0;
  }

  // return the value
  return x;

}

function yCoordsFromDeck() {
  // find the value at the current position in the deck
  var y = yCoords[yPosition];

  // change the position for next time
  yPosition++;

  // if we run out of "cards", shuffle and start over from the top
  if (yPosition == yCoords.length) {
    y = shuffle(yCoords);
    yPosition = 0;
  }

  // return the value
  return y;

}

function draw(){
  var currentEllipse = valueFromDeck();
  var currentX = xCoordsFromDeck();
  var currentY = yCoordsFromDeck();
  ellipse(currentX, currentY, currentEllipse * 5, currentEllipse * 5)

  osc.start();
  env.setADSR(5, .5, .5, 5);
  env.play();


}

