let loopBeat;
let bassSynth, cymbalSynth, crazySynth, mySynth, reverb;
let counter;

function setup() {

  counter = 0;

  bassSynth = new Tone.MembraneSynth().toMaster();
  //  reverb = new Tone.Freeverb().toDestination();
  // reverb.dampening = 1000;
  cymbalSynth = new Tone.NoiseSynth().toMaster();
  cymbalSynth.volume.value = -20;

  crazySynth = new Tone.DuoSynth(
  {
					filterEnvelope: {
					"attack": 0.4,
					"decay": 0.1,
					"sustain": 1,
					"release": .5
					},
					envelope: {
					"attack": 0.04,
					"decay": 0.1,
					"sustain": 1,
					"release": .5
					}
				}
    ).toMaster();

  mySynth = new Tone.FMSynth().toMaster();

  loopBeat = new Tone.Loop(song, '16n');

  Tone.Transport.bpm.value=80
  Tone.Transport.start();
  loopBeat.start();
}

function song(time) {
  // let currentBeat = split(Tone.Transport.position, ':')
  if (counter%4 == 0) {

  bassSynth.triggerAttackRelease('c1', '8n', time);
  }

  if (counter%4 == 0) {
  let x;

  if (counter >8) {
    x = 'c2';
  }else {
    x = 'a2';
  }

  crazySynth.triggerAttackRelease(x, '2n');
  }

  if (counter%4 !== 1) {

    if (counter===3||counter===12) {
      cymbalSynth.envelope.decay=0.5;
    }else {
      cymbalSynth.envelope.decay=0.1;
    }
    cymbalSynth.triggerAttackRelease('1n', time);
  }

  let y;

  if (counter >2) {
    y = 'a2';
  }else {
    y = 'c2';
  }

  mySynth.triggerAttackRelease(y, '16n');
  mySynth.envelope.release= 0.1;

  counter = (counter+1)%16;
}

