// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.1.9/p5.js

let margin = 30;
let n;
let l;

function setup() 
{
	createCanvas(600, 400);
	noLoop();
}

function draw()
{

	noFill();
	strokeWeight(1);
	stroke("#a89780");



	for (let x = 0; x <= width; x++){

		for (let y = 0; y <= height; y++) {

			l = 0.01;

			push();
			noiseDetail(5, 0.6)
			n = noise((x * l), (y * l));
			let r = map(n, 0, 1, 198, 231);
			let g = map(n, 0, 1, 176, 208);
			let b = map(n, 0, 1, 151, 183);
			stroke(r, g, b);
			point(x, y);
			pop();

		}

	}

	for (let x = margin; x <= width - margin; x += (width - (margin * 2)) / 4) {
		for (let y = margin; y <= height - margin; y += (height - (margin * 2)) / 3) {

			l = 0.5;

			n = noise(x * l);

			line(x + n, margin, x + n, height - margin);
			line(margin, y + n, width - margin, y + n);

		}

	}

	textSize(10);
	textFont('Georgia')
	let copy = 'MAP OF GLACIAL AND POSTGLACIAL DEPOSITS IN YOSEMITE VALLEY, MARIPOSA COUNTY, CALIFORNIA'
	let copyWidth = textWidth(copy);
	text(copy, width/2 - copyWidth/2, height - 12);

}

