// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.1.9/p5.js

const dimX = 800;
const dimY = 800;

const margin = 80;
const spacing = 20;

const fr = 30;

var yOff;

function setup() {
  createCanvas(dimX, dimY);
  frameRate(fr);
}

function draw() {
  background(0);
  fill(0);
  stroke(255);
  strokeWeight(1);
  for (let y = margin + spacing; y < height - margin; y += spacing) {
    drawCurve(y);
  }

}

function drawCurve(y) {
  beginShape();
  for (let x = margin; x < width - margin; x++) {

    let multiplier;

    let multiplierBase = 0.2
    let multiplierHigh = 2;

    if (x <= width * 0.3)
      multiplier = multiplierBase;
    else if (x > width * 0.3 && x <= width * 0.34)
      multiplier = map(x, width * 0.3, width * 0.34, multiplierBase, multiplierHigh);
    else if (x > width * 0.34 && x <= width * 0.67)
      multiplier = multiplierHigh;
    else if (x > width * 0.67 && x <= width * 0.71)
      multiplier = map(x, width * 0.67, width * 0.71, multiplierHigh, multiplierBase)
    else
      multiplier = multiplierBase;


    let gaussValue = map(bellCurve(x), -3, 3, 0, 100 * multiplier);
    let deltaNoise = noise(x / 50, y, frameCount / fr);
    yOff = gaussValue * deltaNoise;
    vertex(x, y - yOff);
  }
  endShape();
}

function bellCurve(point) {
  return Math.exp(-(Math.pow(point, 2) / 2));

}

