// Elliot's Instructions

const dim = 800;
const side = 30;
const counter = 1000;

var points = [{}, {}, {}];

const colors = [
  "#f8b195",
  "#f67280",
  "#c06c84",
  "#6c567b"
];

function setup() {
  createCanvas(dim, dim);
  elliot(counter);

}

function draw() {

}

function elliot(counter) {

  background(0);

  elliotCanvas(counter);

//   stroke('black');
//   fill('white');
//   textSize(16);
//   text("Iteration: " + str(counter), 10, str(dim - 20));
}

function elliotCanvas(counter) {

  generateFirstTriangle();
  let count = counter-1;
  while(count){
    generateTriangle();
    count -= 1;
  }
}

function generateFirstTriangle() {
  // First Triangle

  angleMode(DEGREES);

  let alpha = 120;
  let beta = 120;
  let gamma = 120;

  let x = random(2 * side, dim - 2 * side);
  let y = random(2 * side, dim - 2 * side);

  points[0].x = x + side * cos(alpha);
  points[0].y = y + side * sin(alpha);

  points[1].x = x + side * cos(alpha + beta);
  points[1].y = y + side * sin(alpha + beta);

  points[2].x = x + side * cos(alpha + beta + gamma);
  points[2].y = y + side * sin(alpha + beta + gamma);

  fill(random(colors));
  triangle(
    points[0].x, points[0].y,
    points[1].x, points[1].y,
    points[2].x, points[2].y
  );

  shuffle(points, true);
}

function generateTriangle() {

  let m = (points[1].y - points[0].y) / (points[1].x - points[0].x);

  // if infinity then assign m a random value
  if (m == Infinity) {
    m = 3047056004513264;
  } else if (m == -Infinity) {
    m = -3047056004513264;
  }

  let b = points[0].y - m * points[0].x;

  let newPoint = {
    x: ((1 - m * m) * points[2].x + 2 * m * points[2].y - 2 * m * b) / (m * m + 1),
    y: ((m * m - 1) * points[2].y + 2 * m * points[2].x + 2 * b) / (m * m + 1)
  };
  points[2] = newPoint;

  fill(random(colors));
  triangle(
    points[0].x, points[0].y,
    points[1].x, points[1].y,
    points[2].x, points[2].y
  );

  swap(points, int(random(0,2)), 2);
}

function swap(input, index_A, index_B) {  
    let temp = input[index_A];
    input[index_A] = input[index_B];
    input[index_B] = temp;
}

