var leftWall;
var rightWall;
var topWall;
var bottomWall;

var brickSprite;
var brickImage;

var chickSprite;
var chickImage;

var chickSprite2;
var chickImage2;

var walls;
var gameIsOver = false;

function setup() {

  //CREATING CANVAS
  createCanvas(600, 600);

  //CREATING WALLS
  leftWall = createSprite(0, 300, 30, 600);
  leftWall.immovable = true;

  rightWall = createSprite(600, 300, 30, 600);
  rightWall.immovable = true;

  topWall = createSprite(300, 0, 600, 30);
  topWall.immovable = true;

  bottomWall = createSprite(300, 600, 600, 30);
  bottomWall.immovable = true;

  walls = new Group();

  walls.add(leftWall);
  walls.add(rightWall);
  walls.add(topWall);
  walls.add(bottomWall);

  //CREATING THE BRICK
  brickImage = loadImage("assets/sprite3.png");

  brickSprite = createSprite(random(50, width - 50), random(50, height - 50), 100, 100);
  brickSprite.addImage("main", brickImage);
  brickSprite.scale = .3;
  brickSprite.setSpeed(8, random(0, 360));

  chickImage = loadImage("assets/sprite1.png");
  chickSprite = createSprite(500, 400, 100, 100);
  chickSprite.addImage("main", chickImage);
  chickSprite.scale = 0.5;

  chickImage2 = loadImage("assets/sprite2.png");
  chickSprite2 = createSprite(200, 150, 100, 100);
  chickSprite2.addImage("main", chickImage2);
  chickSprite2.scale = 0.5;
}


function draw() {

  background(51);

  if (!gameIsOver) {

    brickSprite.bounce(walls);
    chickSprite.bounce(walls);
    chickSprite2.bounce(walls);

    //setting max speeds for chick sprites
    chickSprite.maxSpeed = 5;
    chickSprite2.maxSpeed = 5;

    noSmooth();

    //KEY INPUTS FOR CHICK 1
    if (keyDown("w")) {
      chickSprite.velocity.y -= 1;
    } else if (keyWentUp("w")) {
      chickSprite.velocity.y = 0;
    }
    if (keyDown("s")) {
      chickSprite.velocity.y += 1;
    } else if (keyWentUp("s")) {
      chickSprite.velocity.y = 0;
    }
    if (keyDown("a")) {
      chickSprite.velocity.x -= 1;
    } else if (keyWentUp("a")) {
      chickSprite.velocity.x = 0;
    }
    if (keyDown("d")) {
      chickSprite.velocity.x += 1;
    } else if (keyWentUp("d")) {
      chickSprite.velocity.x = 0;
    }


    //KEY INPUTS FOR CHICK 2
    if (keyDown("UP_ARROW")) {
      chickSprite2.velocity.y -= 1;
    } else if (keyWentUp("UP_ARROW")) {
      chickSprite2.velocity.y = 0;
    }
    if (keyDown("DOWN_ARROW")) {
      chickSprite2.velocity.y += 1;
    } else if (keyWentUp("DOWN_ARROW")) {
      chickSprite2.velocity.y = 0;
    }
    if (keyDown("LEFT_ARROW")) {
      chickSprite2.velocity.x -= 1;
    } else if (keyWentUp("LEFT_ARROW")) {
      chickSprite2.velocity.x = 0;
    }
    if (keyDown("RIGHT_ARROW")) {
      chickSprite2.velocity.x += 1;
    } else if (keyWentUp("RIGHT_ARROW")) {
      chickSprite2.velocity.x = 0;
    }

    //CHECKING COLLISIONS FOR CHICK SPRITES WITH THE BRICK
    if (chickSprite.collide(brickSprite) || chickSprite2.collide(brickSprite)) {
      gameIsOver = true;
    }
  }



  chickSprite.collide(chickSprite2)

  drawSprites();

  if (gameIsOver) {

    //render a game over overlay
    fill(11)
    rect(0, 0, width, height)
    fill(255)
    textAlign("center")
    textSize(64);
    text("Game Over :(", width / 2, height / 2 - 32)
    textSize(32);
    text("Press R to restart", width / 2, height / 2 + 68)

    if (keyDown("r")) {
      resetGame()
    }
  }
}

function resetGame() {
  gameIsOver = false;

  //reposition the sprites
  chickSprite.position.x = random(100, width - 100)
  chickSprite.position.y = random(100, height - 100)
  brickSprite.position.x = random(100, width - 100)
  brickSprite.position.y = random(100, height - 100)
  chickSprite2.position.x = random(100, width - 100)
  chickSprite2.position.y = random(100, height - 100)
}

