function drawMap() {
  loadPixels();

  mapNoise.x = position.x;
  for (let i = 0; i < width; i++) {
    mapNoise.y = position.y;
    for (let j = 0; j < height; j++) {
      index = 4 * (j * width + i);

      let pixelColor = getTerrain(noise(mapNoise.x, mapNoise.y));


      if ((i > (treasurePoint.x - 12) && i < (treasurePoint.x + 12)) &&
        (j > (treasurePoint.y - 12) && j < (treasurePoint.y + 12)))
        pixelColor = color('red');

      if ((i > (treasurePoint.x - 8) && i < (treasurePoint.x + 8)) &&
        (j > (treasurePoint.y - 8) && j < (treasurePoint.y + 8)))
        pixelColor = color('black');

      pixels[index] = red(pixelColor);
      pixels[index + 1] = green(pixelColor);
      pixels[index + 2] = blue(pixelColor);
      pixels[index + 3] = 255;

      mapNoise.y += 0.005;
    }
    mapNoise.x += 0.005;
  }
  updatePixels();
}

function getTerrain(noise) {
  let newColor;

  if (noise > 0.7) {
    newColor = colors.water;
  } else if (noise > 0.6) {
    newColor = colors.sand;
  } else if (noise > 0.4) {
    newColor = colors.grass;
  } else if (noise > 0.3) {
    newColor = colors.mountain;
  } else {
    newColor = colors.snow;
  }

  return color(newColor);
}


