function changeColor() {
  r = random(255);
  g = random(255);
  b = random(100);
}
function moveItBaby(xx, yy) {
  t.penUp();
  t.moveTo(xx, yy);
  t.penDown();
}

function makeFlowers(x, y) {
  //scale(random(0.5, 1));
  t.pushState(); //1

  strokeWeight(1);
  moveItBaby(x, y);

  for (var i = 0; i < 50; i++) {
    for (var f = 0; f < 50; f++) {
      t.moveForward(f * 2);
      t.turnLeft(f * 2);
    }
  }

  t.pushState(); //2
  strokeWeight(5);
  moveItBaby(x + 40, 500);
  t.turnRight(10);
  t.moveForward(500 - y);
  t.popState(); //2
  t.popState(); //1
}

