function stepscene() {
  for (const face of faces) {
    stepface(face);
  }
}
function stepface(b) {
  b.x += b.fX;
  b.y += b.fY;

  if (b.x > width - b.radius) b.fX = -abs(b.fX);
  if (b.y > height - b.radius) b.fY = -abs(b.fY);
  if (b.x < 0 + b.radius) b.fX = abs(b.fX);
  if (b.y < 0 + b.radius) b.fY = abs(b.fY);
}
function drawscene() {
  background(230,290,60);
  for (const face of faces) {
    drawface(face);
  }
}
