var recording = false;
var recorder;
var chunks = [];

const fr = 30;

var noiseMap = {
  x: 0,
  y: 0,
  z: 0
}

function setup() {
  createCanvas(windowWidth, windowHeight);
  colorMode(HSB);
  strokeWeight(3);

  frameRate(fr);

  // initalize recorder
  record();
}

function draw() {
  drawHair();
}

function drawHair() {
  clear();
  background(0);
  let theta, x, y;
  let multiplier = 90;
  let spacing = 20;
  let noiseIncrement = {
    x: 0.01,
    y: 0.02,
    z: 0.003
  };

  noiseMap.x = 0;
  for (let i = 0; i < width; i += spacing) {
    noiseMap.y = 0;
    for (let j = 0; j < height; j += spacing) {
      theta = map(noise(noiseMap.x, noiseMap.y, noiseMap.z), 0, 1, 0, 2 * PI);
      x = multiplier * sin(theta);
      y = multiplier * cos(theta);

      stroke(map(noise(noiseMap.x, noiseMap.y, noiseMap.z), 0, 1, 0, 259), 255, 255);
      line(i, j, i + x, j + y);

      noiseMap.y += noiseIncrement.y;
    }
    noiseMap.x += noiseIncrement.x;
  }
  noiseMap.z += noiseIncrement.z;
}

function record() {
  chunks.length = 0;

  let stream = document.querySelector('canvas').captureStream(fr);

  recorder = new MediaRecorder(stream);

  recorder.ondataavailable = e => {
    if (e.data.size) {
      chunks.push(e.data);
    }
  };

  recorder.onstop = exportVideo;
}

function exportVideo(e) {
  var blob = new Blob(chunks, {
    'type': 'video/mp4'
  });

  // Draw video to screen
  var videoElement = document.createElement('video');
  videoElement.setAttribute("id", Date.now());
  videoElement.controls = true;
  document.body.appendChild(videoElement);
  videoElement.src = window.URL.createObjectURL(blob);

  // Download the video 
  var url = URL.createObjectURL(blob);
  var a = document.createElement('a');
  document.body.appendChild(a);
  a.style = 'display: none';
  a.href = url;
  a.download = 'noisyHair.mp4';
  a.click();
  window.URL.revokeObjectURL(url);
}

function keyPressed() {
  recording = !recording

  if (keyCode === 32 && recording) { // SPACEBAR
    console.log("recording started!");
    recorder.start();
  }

  // if we are recording, stop recording 
  if (keyCode === 32 && !recording) { // SPACEBAR
    console.log("recording stopped!");
    recorder.stop();
  }

  if (keyCode === 83 && recording) { // Letter S
    saveCanvas('noisyHair', 'jpg');
    recording = !recording;
  }

}

