function balloon(x, y, f, dX, dY){
    this.x = x;
    this.y = y;
    this.color = f;
    this.dX = dX;
    this.dY = dY;

    this.checkBoundary = function(){
        if (this.x > width - 5) {
          this.dX = -abs(this.dX);
        }
          if (this.y > height - 5) {
            this.dY = -abs(this.dY);
          }
          if (this.x < 5) {
            this.dX = abs(this.dX);
          }
          if (this.y < 5) {
            this.dY = abs(this.dY);
          }
    }

    this.update = function(){
        this.x += this.dX;
        this.y += this.dY;
    };


    this.display = function(){
        push();
        stroke(0);
        line(this.x,this.y,this.x,this.y+100)
        noStroke();
        fill(this.color);
        ellipse(this.x, this.y, 50, 60);
        triangle(this.x-20, this.y+40, this.x, this.y, this.x+20, this.y+40)
    }

}

