      n = min(n, 500);

      beginShape();
      for (var j = 0; j < n; j++) {
        var a = j / n * TAU;
        var p = p5.Vector.fromAngle(j / n * TAU);
        p.mult(noise(xOff + p.x * inc, yOff + p.y * inc) * size);
        vertex(p.x, p.y);
      }
      endShape(CLOSE);

      xOff += offInc;
      yOff += offInc;

      size *= multiplier;
    }
  } else {
    noLoop();
  }
}

