var paddleA, paddleB, ball, wallTop, wallBottom, balls;
var MAX_SPEED = 10;

function setup() {
  createCanvas(800, 400);
  // frameRate(6);

  paddleA = createSprite(30, width/2 - 20, 100, 10);
  paddleA.immovable = true;

  paddleB = createSprite(30, height/2 - 20, 10, 100);
  paddleB.immovable = true;

  paddleC = createSprite(30, 20, 100, 10);
  paddleC.immovable = true;

  paddleD = createSprite(width - 30, height/2 - 20, 10, 100);
  paddleD.immovable = true;
  ball = createSprite(width, height, 10, 10);

  balls = new Group()
}

function draw() {
  background(0);

  paddleA.position.x = constrain(mouseX, paddleA.width/2, width-paddleA.width/2);
  paddleB.position.y = constrain(mouseY, paddleB.height/2, height-paddleB.height/2);
  paddleC.position.x = constrain(mouseX, paddleC.width/2, width-paddleC.width/2);
  paddleD.position.y = constrain(mouseY, paddleD.height/2, height-paddleD.height/2);
  // ball.position.x = constrain(width);
  // ball.position.y = constrain(height);

  for (i=0; i<100; i++) {
    paddleB.shapeColor = color(255, 255, 255);
    paddleA.shapeColor = color(255, 255, 255);
    paddleC.shapeColor = color(255, 255, 255);
    paddleD.shapeColor = color(255, 255, 255);
    ball.shapeColor = color(255, 0, 0);
    ball.maxSpeed = MAX_SPEED;
    ball.setSpeed(MAX_SPEED);
    ball.bounce(paddleA);
    ball.bounce(paddleB);
    ball.bounce(paddleC);
    ball.bounce(paddleD);


    var swing;
    if(ball.bounce(paddleA)) {
      swing = (ball.position.y-paddleA.position.y)/2;
      ball.setSpeed(MAX_SPEED, ball.getDirection()+swing);
    }
    if(ball.bounce(paddleB)) {
        swing = (ball.position.x-paddleB.position.x)/2;
        ball.setSpeed(MAX_SPEED, ball.getDirection()+swing);
      }
    if(ball.bounce(paddleC)) {
    swing = (ball.position.y-paddleC.position.y)/2;
    ball.setSpeed(MAX_SPEED, ball.getDirection()+swing);
    }
    if(ball.bounce(paddleD)) {
        swing = (ball.position.x-paddleD.position.x)/2;
        ball.setSpeed(MAX_SPEED, ball.getDirection()+swing);
        }
    if(ball.position.x<0) {
      ball.position.x = random(width);
      ball.position.y = 0;
      ball.setSpeed(MAX_SPEED, 0);
    }

    if(ball.position.x>width) {
      ball.position.x = random(width);
      ball.position.y = 0;
      ball.setSpeed(MAX_SPEED, 60);
    }

    drawSprites();

  }


}

// require https://cdnjs.cloudflare.com/ajax/libs/p5.js/1.1.9/p5.js

function setup() 
{
	createCanvas(500, 500);
	background("#ced1d5");
	noLoop();
	noFill();
	rectMode(CENTER);
}

function draw()
{

	for (let x = 0; x <= width; x += width/5) {

		for (let y = 0; y <= height; y += height/5){

			let rx = 10;
			let ry = 5;

			for (let a = 0; a < 10; a++){

				colorMode(HSL);
				stroke(30, 100, 50, random(1));
				strokeWeight(1);
				rect(x + rx, y + ry, random(10, 100));
				rx = random(-10, 10);
				ry = random(-5, 5);

			}

		}

	}

}
