let step = 5;

let arr = [3, 15, 161, 90, 57, 82, 203, 250, 133, 16, 0, 168];

function setup() {
  createCanvas(400, 400);
  // noLoop();
}

function draw() {
  background(255);

  let x, y;


  //grid
  for (let x = 0; x < width; x += step + 1) {
    for (let y = 0; y < height; y += step + 1) {
      push();
      strokeWeight(0.15);
      stroke(arr[frameCount % arr.length] / 3);
      line(x / noise(frameCount * 0.001, cos(x)), y + noise(frameCount * 0.001, sin(y)), 200, 200);
      pop();
    }
  }
}

