// Dot Challenge Starting Point

let w=400,h=400;
let hue;
function setup() {
  colorMode(HSB)
  createCanvas(400, 400);
}

function draw() {
  background(15);

  noStroke();
  ellipseMode(CENTER);

  let noiseFrequency = 3


  for (let i = 0; i < 1000; i+=12) {
    // these points are not scattered in the same way
    // how can you make the arrangement match the challenge?
    x = noise(i * noiseFrequency+20, 100) * w;
    y = noise(i * noiseFrequency, 1000) * h;

    let diameter = 6 + random(12);

    //mapping the diameter with the required hue range. 
    //In HSB colorMode Hue has a range of 0 to 360
    hue=map(diameter,5,15,20,225)

    fill(hue,200,180);

    ellipse(x, y, diameter);
  }

  noLoop();
}

