// refering to Daniel Schiffman's video on additional waves
// https://www.youtube.com/watch?v=okfZRl4Xw-c&t=11s

// inspired by Anni Albers' work
// https://www.theguardian.com/artanddesign/2019/jan/18/anni-albers-intersecting

let waves1 = [];
let waves2 = [];
let waves3 = [];
let waves4 = [];
let colorArray = ["#f9ece3", "#14599E", "#eb5f2a", "#395785"];
let c1, c2, c3, c4;

function setup() {
  createCanvas(800, 600);
  background("#eb5f2a");
  rectMode(CENTER);

  // drawing waves from the wave class
  for (i = 0; i < 10; i++) {
    waves1[i] = new Wave(random(50), random(50, 100), random(TWO_PI));
    waves2[i] = new Wave(random(30), random(800), random(TWO_PI));
    waves3[i] = new Wave(random(30), random(800), random(TWO_PI));
    waves4[i] = new Wave(random(80), random(10, 300), random(TWO_PI));
  }

  DrawChecker();

  // drawing the blue stitch patches
  let h1 = random(100);
  let h2 = random(100);
  DrawPatch(h1, 30);
  DrawPatch(h2, 10);
  DrawPatch(h2 + 20, 5);
  // console.log(h1)
  // console.log(h2)


  // drawing the vertical bands of blue and white
  for (a = 0; a < width; a++) {
    if (a % 2 == 0) {
      fill("#103D69");
    } else {
      fill("#f9ece3");
    }
    DrawBand(a * 50);
  }

  // setting the color of the wave stitches
  c1 = random(colorArray);
  c2 = random(colorArray);
  c3 = random(colorArray);
  c4 = random(colorArray);
}

function draw() {
  // background(255)
  for (i = 0; i < width; i += 3) {
    let y1 = 0;
    let y2 = 0;
    let y3 = 0;
    let y4 = 0;

    // creating an additional waves 
    for (let wave1 of waves1) {
      y1 += wave1.measure(i);
    }

    for (let wave2 of waves2) {
      y2 += wave2.measure(i);
    }

    for (let wave3 of waves3) {
      y3 += wave3.measure(i);
    }

    for (let wave4 of waves4) {
      y4 += wave4.measure(i);
    }

    // drawing lines along the wave
    strokeWeight(3);
    noFill();
    stroke(c1);
    line(i, y1 + height / 4, i, y1 + height / 4 + 5);
    stroke(c2);
    line(i, y2 + height / 2, i, y2 + height / 2 + 5);
    stroke(c3);
    line(i, y3 + (3 * height) / 4, i, y3 + (3 * height) / 4 + 5);
    stroke(c4);
    line(i, y4 + 15 + (3 * height) / 4, i, y4 + 15 + (3 * height) / 4 + 5);
  }
}

// defining the wave class
class Wave {
  constructor(amplitude, period, phase) {
    this.amplitude = amplitude;
    this.period = period;
    this.phase = phase;
  }

  // wave equation with phase, period and amplitude
  measure(x) {
    return sin(this.phase + (TWO_PI * x) / this.period) * this.amplitude;
  }
}

// creating a function to create the stitched almost checker background in orange
function DrawChecker() {
  for (let x = 0; x < width; x++) {
    for (let y = 0; y < height; y++) {
      if ((x % 2 == 0 && y % 2 == 1) || (x % 2 == 1 && y % 2 == 0)) {
        fill("#c43615");
      } else {
        fill("#eb5f2a");
      }
      // line()
      push();
      noStroke();
      rect(x * 5 + random(-1, 1), y * 5 + random(-1, 1), 2, 5);
      // line(x,y,x+5,y+5)
      pop();
    }
  }
}

// function to the vertical bands
function DrawBand(x) {
  for (let i = 0; i < 6; i++) {
    for (let j = 0; j < height; j++) {
      if ((i % 2 == 0 && j % 2 == 1) || (i % 2 == 1 && j % 2 == 0)) {
        push();
        noStroke();
        // fill("#27547f");
        rect(x + i * 5 + random(-1, 1), j * 5 + random(-1, 1), 2, 5);
        pop();
      }
    }
  }
}

function DrawPatch(h, x) {
  for (let i = 0; i < width; i++) {
    for (let j = h; j < h + x; j++) {
      if ((i % 2 == 0 && j % 2 == 1) || (i % 2 == 1 && j % 2 == 0)) {
        fill("#1c66b1");
      } else {
        fill("#14599E");
      }
      push();
      noStroke();

      rect(i * 5 + random(-1, 1), j * 5 + random(-1, 1), 2, 5);
      pop();
    }
  }
}

