}

function DrawLocation(x, y) {
  stroke("#6E291B");
  strokeWeight(10);
  strokeCap(SQUARE);
  line(x, y, x + 50, y + 50);
  line(x + 50, y, x, y + 50);
  // arc(x+25,y+25, 80, 80, PI + QUARTER_PI, TWO_PI, OPEN)
}

function DrawLine(y) {
  x = 0;
  y = 50;
  phase = 10;
  period = 600;
  amplitude = TWO_PI / 10;

  for (i = 0; i < width; i += 2) {
    x = sin(phase + (TWO_PI * i) / period) * amplitude;
    y += x;
    text(" - ", i + x, y);
  }
}

function DrawViewfinder(x, y, l) {
  push();
  noFill();
  stroke(0, 100);
  strokeWeight(100);
  strokeCap(SQUARE);
  rect(0, 0, width, height);
  pop();

  stroke(0);
  strokeWeight(10);
  strokeCap(PROJECT);
  line(x, y, x + l, y);
  line(x, y, x, y + l);

  line(width - x, y, width - (x + l), y);
  line(width - x, y, width - x, y + l);

  line(x, height - y, x + l, height - y);
  line(x, height - y, x, height - (y + l));

  line(width - x, height - y, width - (x + l), height - y);
  line(width - x, height - y, width - x, height - (y + l));
}

