function single(img, a, threshold) {
  img.loadPixels();

  for (let y = 0; y < wallImg.height; y++) {
    for (let x = 0; x < wallImg.width; x++) {
      const in_color = wallImg.get(x, y);

      const r = red(in_color);
      const g = green(in_color);
      const b = blue(in_color);

      avg = (r + g + b) / 3;

      let out_color;

      if (avg > (threshold * 2) / 3) {
        // if (r > threshold && g > threshold && b > threshold){
        out_color = color(0, 117, 113, a);
      } else if (avg > threshold / 3 && avg < (threshold * 2) / 3) {
        out_color = color(183, 235, 52, a);
      } else {
        out_color = color(224, 90, 191, a);
      }

      img.set(x, y, out_color);
    }
  }

  img.updatePixels();


  image(img, 0, 0, width, height);

}

