function draw() {
  background("#60BFBF");

  ellipseMode(CENTER);

  amplitude = amplitude_slider.value() / 100;
  frequency = frequency_slider.value() / 100;
  timeVal = time_slider.value() / 100;

  noiseDetail(1, 0.5);

  // study this loop. do you understand how the line of ellipses is created?
  //yes, I do

  DrawRope(1, 0, "#BAD9D9");

  push()
  noStroke()
  fill("#FFD249")
  ellipse(150,150, 100,100)
  pop()

  frameSpeed += timeVal / 5;
}

function DrawRope(dir, h, col) {
  for (i = 0; i < 1; i += 0.05) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    // tried multiplying by the frameSpeed but that was too too fast
    n = noise(i * frequency * 30 + frameSpeed);
    offsetX = (n - 0.5) * amplitude * dir * 100;
    offsetY = (n - 0.5) * amplitude * dir * -500;

    push();
    fill(col);
    noStroke();
    translate(0, h);
    ellipse(x + offsetX, y + offsetY, 50, 50);

    push();
    // rectMode(CENTER)
    rect(x + offsetX, y + offsetY - 400, 80, 400, 10);
    pop();

    pop();
  }
}

