function drawWaves(number){
  for(let i=number; i>=0;i--){
    drawWave(i,number);
  }
 //noLoop();
  baseT +=0.01;
}

function drawWave(n,rows){
  let baseY =height -n*waveMaxHeight/3;
  let t=baseT+n*100;
  let startX=0;
  push();
 // // colorMode(HSB);
 //  let c =map(myColors[],0,rows,0,5);

//   randCol=random(myColors.length);
//   randCol=floor(randCol);
    colorMode(HSB);
  // Calculate the hue (0 - 360) based on the wave number, mapping
  // it to an HSB hue value
  let hue = map(n, 0, rows, 170, 250);
  fill(hue, 50, 80);
//   fill(myColors[randCol]);
  noStroke();
  beginShape();
  vertex(startX,baseY);
  for(let x=startX;x<=width;x+=10){
    let y=baseY-map(noise(t/2),0,1,10,waveMaxHeight);
    vertex(x,y);
    t+=0.01;
  }
  vertex(width,baseY);
  vertex(width,height);
  vertex(0,height);
  endShape();
}

