  switch(mode){
    case 0:
    for(let i=0;i<points.length;i++){
      ellipse(points[i].x,points[i].y,2);
    }
      break;

    case 1:
      noiseSpread();
      break;

    case 2:
      randomSpread();
      break;
  }

  //noiseSpread();
}

function noiseSpread(){
    noStroke();


  for(let i=0;i<points.length;i++){
    let r = map(points[i].x,0,width,50,255);
    let g = map(points[i].y,0,height,50,255);
    let b = map(points[i].y,0,height,50,255);

    fill(r,g,b);
    ellipse(points[i].x,points[i].y,1);
    var angle = map(noise(points[i].x*flow,points[i].y*flow),0,1,0,720);
    points[i].add(createVector(cos(angle),sin(angle)));
  }
}

function randomSpread(){
    noStroke();
  fill(255);
  for(let i=0;i<points.length;i++){
    ellipse(points[i].x,points[i].y,1);
    var angle = random(0,360);
    points[i].add(createVector(cos(angle),sin(angle)));
  }
}

function noiseMode(){
  mode=1;
  button1.hide();
  button2.hide();
}

function randomMode(){
  mode=2;
  button1.hide();
  button2.hide();
}

