function circleLine(w, h) {
  let amplitude = amplitude_slider.value() / 100;
  let frequency = frequency_slider.value() / 100;
  let timespeed = speed_slider.value() / 100;

  noiseDetail(1, 0.5);

  let r = map(sin(frameCount / 10), -1, 1, 0, 255);
  let g = map(cos(frameCount / 10), -1, 1, 0, 255);
  let b = map(sin(frameCount / 10), 1, -1, 0, 255);

  fill(r, g, b);

  // study this loop. do you understand how the line of ellipses is created?
  for (i = 0; i < 1; i += 0.03 * frequency) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    let t = map(timespeed, 0, 1, 0, frameCount * 0.15);

    // hint: drive offsetX and offsetY with noise() instead of random()
    let offsetX = noise(x * frequency, t) * amplitude * 20;
    let offsetY = noise(y * frequency, t, 10) * amplitude * 100;

    ellipse(x + random(offsetX), y + offsetY, w, h);
  }
}

