/* ----------------------------------- setup ---------------------------------- */

function setup() {
	createCanvas(600, 600, WEBGL);

	cam = createCamera();

	class Particle {
		constructor(radiusIn, radiusExt, theta_long, phi_lat, thickness, lineScale, strokeW) {
			this.radiusIn = radiusIn;
			this.radiusExt = radiusExt;
			this.theta_long = theta_long;
			this.phi_lat = phi_lat;
			this.thickness = thickness;
			this.lineScale = lineScale; //scale of length of each line, between 0 and 1
			this.strokeW = strokeW;
		}

		drawPoint(rotateSpeed) {
			let v = generateGeoVector(
				this.radiusIn,
				this.radiusExt,
				this.theta_long + TWO_PI * rotateSpeed, //particles speed = torus * 2
				this.phi_lat + TWO_PI * rotateSpeed,
				this.thickness
			);
			stroke(220);
			strokeWeight(this.strokeW);
			point(v.x, v.y, v.z);
		}

		drawLine(rotateSpeed, num) {
			let lineDensity = this.lineScale / num; // how many subdivisions make up the line
			let pVectors = [];
			for (let i = 0; i <= num; i++) {
				pVectors[i] = generateGeoVector(
					this.radiusIn,
					this.radiusExt,
					this.theta_long + TWO_PI * rotateSpeed - lineDensity * i,
					this.phi_lat - TWO_PI * rotateSpeed + lineDensity * i,
					this.thickness
				);
			}

			for (let i = 0; i < num; i++) {
				//render the lines
				line(
					pVectors[i].x,
					pVectors[i].y,
					pVectors[i].z,
					pVectors[i + 1].x,
					pVectors[i + 1].y,
					pVectors[i + 1].z
				);
			}
			stroke(220);
			strokeWeight(this.strokeW);
			stroke(255);

		}
	}

	for (let i = 0; i < params.numParticle; i++) {
		particles[i] = new Particle(
			params.radiusIn, //radiusIn
			params.radiusExt, //radiusExt
			random(0, TWO_PI), //theta_long
			random(0, TWO_PI), //phi_lat
			params.thickness, //thickness
			params.lineScale, //ineScale: (0,1)
			constrain(6 * randomGaussian(), 1, 6) //stroke weight: random between 1-5
		);
	}
}

/* ----------------------------------- draw ---------------------------------- */

function draw() {
	background(0);

  cam.camera(camSetting.xAxis, camSetting.yAxis, camSetting.zAxis, camSetting.centerX, camSetting.centerY, camSetting.centerZ, 0, 1, 0);

  /* ----- planet --- */
   push();
  rotateX(radians(params.arcAngleXDegrees));
  rotateY(radians(params.arcAngleYDegrees));
  stroke(255);
  strokeWeight(14);
  noFill();
  //arc(x, y, w, h, start, stop, [mode], [detail])
  arc(0,0,params.radiusIn*2.8,params.radiusIn*3.2,0,TWO_PI,CHORD,50);
  pop();

  drawGeo(params.radiusIn, params.radiusExt, params.numSubdivision,params.thickness); //radiusIn, radiusExt, pts

  params.rotateSpeed += radians(1);
  for (let i = 0; i < params.numParticle; i++) {
      particles[i].drawLine(params.rotateSpeed, params.numSubdivision);
	}

}


function generateGeoVector(
	radiusIn,
	radiusExt,
	theta_long,
	phi_lat,
	thickness
) {
	let x = (radiusExt + radiusIn * cos(theta_long + params.rotateSpeed)) * cos(phi_lat + params.rotateSpeed);
	let y = (radiusExt + radiusIn * cos(theta_long + params.rotateSpeed)) * sin(phi_lat + params.rotateSpeed);
	let z = thickness * sin(theta_long);
	let v = createVector(x, y, z);
	return v;
}

function drawGeo(radiusIn, radiusExt, pts, thickness) {
	// stroke(220, 150, 0);
	// strokeWeight(0.2);
    noStroke();
	fill(0);

	beginShape(TRIANGLE_STRIP); //TRIANGLE_STRIP
	for (let i = 0; i <= pts; i++) {
		for (let j = 0; j <= pts; j++) {
			let long = map(i, 0, pts, 0, TWO_PI);
			let long1 = map(i + 1, 0, pts, 0, TWO_PI);
			let lat = map(j, 0, pts, 0, TWO_PI);

			let v1 = generateGeoVector(radiusIn, radiusExt, long, lat, thickness);
			let v2 = generateGeoVector(radiusIn, radiusExt, long1, lat, thickness);
			vertex(v1.x, v1.y, v1.z);
			vertex(v2.x, v2.y, v2.z);
		}
	}
	endShape();
}

