inc = 0.1;
scl = 20;
let col, row;
var fr, ti;
let xoff, yoff;
let zoff = 0;
let display;


var particles = [];
var flowfield = [];

function setup() {
  createCanvas(windowWidth, windowHeight);
  background(0);


  col = floor(width / scl);
  row = floor(height / scl);
  fr = createP("");
  ti = createP(display)

  flowfield = new Array(row * col);

  for (i = 0; i < 500; i++) {

    particles[i] = new Particle();
  }
}

function draw() {
  // randomSeed(10)




  yoff = 0;
  for (y = 0; y < row; y++) {
    xoff = 0;
    for (x = 0; x < col; x++) {
      var index = x + y * col;

      var angle = noise(xoff, yoff, zoff) * TWO_PI * 2;

      var v = p5.Vector.fromAngle(angle);
      v.setMag(1);
      flowfield[index] = v;
      xoff += inc;

      // stroke(255, 0, 0, 100);
      // push();
      // translate(x * scl, y * scl);
      // rotate(v.heading());
      // line(0, 0, scl, 0);
      // pop();
    }
    yoff += inc;
    zoff += 0.001;
  }

  for (i = 0; i < particles.length; i++) {
    particles[i].follow(flowfield);
    particles[i].update();
    particles[i].edges();
    particles[i].show();
    // particles[i].updatePos();
  }

  // fr.html(floor(frameRate()));
  ti.html(hour() + ": " + minute() + ": " + second())

}

