let myTurtle;
let lineColor;
let xPos = [];
let yPos = [];

function setup() {
	createCanvas(600, 600);
	lineColor = color('#E6CEE4');
	myTurtle = new Turtle();
}

function draw() {
	background(20);
	stroke(255);
	assignSuperFormula();
	drawLine();
	noLoop();
}

function drawLine() {
	let angle = 0;

	for (let i = 0; i < xPos.length; i++) {
		stroke(lineColor);
		strokeWeight(random(0.5, 2));

		// if (i % 4 === 0) {
			myTurtle.penUp();
			myTurtle.moveTo(xPos[i], yPos[i]);
			myTurtle.turnTo(sin(angle)); //270-angle, sin(angle)
			myTurtle.penDown();

			let angleTotal_left = 0;
      let angleTotal_right = 0;
			let turnAngle_left = 0.6;
			let turnAngle_right = 0.2;

			for (let i = 0; i < 800; i++) {
				lineColor.setAlpha(random(50, 100));
				if (angleTotal_left <= 60) {
				  myTurtle.moveForward(0.2);
				  myTurtle.turnRight(turnAngle_left);
					angleTotal_left += turnAngle_left;
					console.log(angleTotal_left, 'turn left');
				} else if(angleTotal_left > 60) {
					myTurtle.moveForward(1.1);
					myTurtle.turnRight(turnAngle_right);
          angleTotal_right += turnAngle_right;
					console.log(angleTotal_right, 'turn right');
				}
        if( angleTotal_right === angleTotal_left){
          angleTotal_left = 0;
        }
			}
		//}

		//angle += (PI * 2) / 0.01;
    angle++;
	}
}

function assignSuperFormula() {
	//reference: https://openprocessing.org/sketch/1097008
	let m = int(random(3, 9));
	let n1 = int(random(1, 7));
	let n2 = int(random(1, 7));
	let n3 = random(0.5, 2);

	for (let t = 0; t < PI * 2; t += 0.01) {
		r =
			20 *
			(12 - n1 - n2 + n3) *
			pow(
				abs(pow(cos((m * t) / 4), n1)) + abs(pow(sin((m * t) / 4), n2)),
				-1 / n3
			);
		xPos.push(width / 2 + r * cos(t));
		yPos.push(height / 2  + r * sin(t));
	}
}

