let amplitude_slider;
let frequency_slider;
let timespeed_slider;

let startX = 0;
let startY = 150;
let endX = 450;
let endY = 150;
let ts = 0;

function setup() {
  createCanvas(500, 300);
  blendMode(HARD_LIGHT);
  createP("Frequency");
  frequency_slider = createSlider(0, 100, 50);
  createP("Amplitude");
  amplitude_slider = createSlider(100, 200, 120);
  createP("Time Speed");
  timespeed_slider = createSlider(0, 100, 40);
}

function draw() {
  background(50, 20);
  ellipseMode(CENTER);

  let frequency = frequency_slider.value() / 100;
  let amplitude = amplitude_slider.value() / 100;
  let timespeed = timespeed_slider.value() / 100;

  ts += timespeed * 0.1;

  noiseDetail(1, 0.5);

  noStroke();


  for (i = 0; i < 1; i += 0.02) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    const n = i * frequency * 10 + ts;

    let offsetX = (noise(n) - 0.5) * amplitude * -80;
    let offsetY = (noise(n) - 0.5) * amplitude * -80;

    stroke(random(25), 240, 224);
    line(x + offsetX + 100, y + offsetY, x - offsetX - 100, y - offsetY);
    stroke(0, 200, 20);
    line(x + offsetX + 150, y + offsetY, x - offsetX - 10, y - offsetY);
    stroke(164, 237, 237);
    line(
      x + offsetX + 100,
      y + offsetY - 100,
      x - offsetX - 100,
      y - offsetY - 100
    );
    stroke(200, 40, 200);
    line(
      x + offsetX + 150,
      y + offsetY - 100,
      x - offsetX - 100,
      y - offsetY - 100
    );
    stroke(250, 247, 87);
    line(
      x + offsetX + 150,
      y + offsetY + 100,
      x - offsetX - 100,
      y - offsetY + 100
    );
    stroke(232, 88, 155);
    line(
      x - offsetX + 100,
      y + offsetY + 100,
      x - offsetX - 100,
      y - offsetY + 100
    );
  }
}

